import { useTimer } from "../../../hooks/use-timer";

import recipe from "../../../recipies/v60.json";

const RecipeTimer = () => {
    const timer = useTimer({
        steps: recipe.steps,
    });

    console.log(timer);

    return (
        <article>
            <div>
                <time></time>
                <span>total left</span>
            </div>
            <h1>[current step duration]</h1>
            <h2>[current step description]</h2>
            <ul>
                {recipe.steps.map((step, i) => (
                    <li key={i}>
                        <time>{step.duration}</time>
                        <span>&nbsp;</span>
                        <span>{step.description}</span>
                    </li>
                ))}
            </ul>
            {!timer.isComplete && !timer.isRunning && (
                <button onClick={timer.start}>Start</button>
            )}
            {timer.isRunning && <button onClick={timer.pause}>Pause</button>}
        </article>
    );
};

export default RecipeTimer;
