import { useTimer } from "../../../hooks/use-timer";

import { parseMillisecondsIntoTimeStamp } from "../../../utils/parser";

import recipe from "../../../recipies/v60.json";

const RecipeTimer = () => {
    const timer = useTimer({
        steps: recipe.steps,
    });

    return (
        <article>
            <div>
                <time>{parseMillisecondsIntoTimeStamp(timer.remaining)}</time>
                <span>&nbsp;</span>
                <span>total left</span>
            </div>
            <h1>[current step duration]</h1>
            <h2>[current step description]</h2>
            <ul>
                {recipe.steps.map((step, i) => {
                    const content = (
                        <>
                            <time>
                                {parseMillisecondsIntoTimeStamp(
                                    step.duration * 1000
                                )}
                            </time>
                            <span>&nbsp;</span>
                            <span>{step.description}</span>
                        </>
                    );

                    // This is the current step if the elapsed time is more than the sum of
                    // the durations of all previous steps:
                    const isCurrentStep = recipe.steps
                        .slice(0, i)
                        .reduce((total, step) => total + step.duration, 0);
                    console.log(isCurrentStep);

                    // if (timer.currentStep === i)
                    //     return (
                    //         <li key={i}>
                    //             <strong>{content}</strong>
                    //         </li>
                    //     );

                    return <li key={i}>{content}</li>;
                })}
            </ul>
            {!timer.isRunning && timer.elapsed === 0 && (
                <button onClick={timer.start}>Start</button>
            )}
        </article>
    );
};

export default RecipeTimer;
