import { useState, useEffect, useMemo } from "react";

import { useInterval } from "./use-interval";

type Step = {
    description: string;
    duration: number;
};

export const useTimer = (userSteps: Step[]) => {
    // Initialize timer:
    const steps = useMemo(
        () =>
            userSteps.map((step, i) => ({
                ...step,
                duration: step.duration * 1000,
                start: userSteps
                    .slice(0, i)
                    .reduce(
                        (total, { duration }) => total + duration * 1000,
                        0
                    ),
            })),
        [userSteps.length]
    );
    const totalTime = useMemo(
        () =>
            userSteps.reduce(
                (total, { duration }) => total + duration * 1000,
                0
            ),
        [userSteps.length]
    );
    const [elapsed, setElapsed] = useState(0);
    const [isRunning, setIsRunning] = useState(false);
    const remaining = useMemo(() => totalTime - elapsed, [elapsed]);
    const isComplete = useMemo(() => remaining === 0, [remaining]);
    const currentStep = useMemo(() => 0, [elapsed]);

    // Run the timer:
    const tick = () => setElapsed((elapsed) => (elapsed += 100));
    useInterval(tick, isRunning && !isComplete ? 100 : null);

    // Manipulate the timer:
    const pause = () => setIsRunning(false);
    const reset = () => {
        setElapsed(0);
        setIsRunning(false);
    };
    const start = () => setIsRunning(true);
    const toggle = () => setIsRunning((isRunning) => !isRunning);

    return {
        currentStep,
        elapsed,
        isComplete,
        isRunning,
        pause,
        remaining,
        reset,
        start,
        toggle,
    };
};
