import Link from "next/link";

import fs from "fs";

import { useRouter } from "next/router";
import { useState } from "react";

const RecipeSettings = () => {
    const router = useRouter();
    const { id } = router.query;

    if (!id) return null;

    const recipe = require(`../../../recipies/${id}.json`);

    return (
        <article>
            <h1>{id}</h1>
            <div>
                <input
                    type="range"
                    min={recipe.minWater}
                    max={recipe.maxWater}
                />
            </div>
            <Link href="timer">
                <a>Let's do it!</a>
            </Link>
        </article>
    );
};

export default RecipeSettings;
