import { useTimer } from "../../../hooks/use-timer";

import { parseMillisecondsIntoTimeStamp } from "../../../utils/parser";

import recipe from "../../../recipies/v60.json";

const RecipeTimer = () => {
    const timer = useTimer(recipe.steps);

    return (
        <article>
            <div>
                <time>{parseMillisecondsIntoTimeStamp(timer.remaining)}</time>
                <span>&nbsp;</span>
                <span>total left</span>
            </div>
            <h1>
                {parseMillisecondsIntoTimeStamp(
                    recipe.steps[timer.currentStep].duration
                )}
            </h1>
            <h2>{recipe.steps[timer.currentStep].description}</h2>
            <ul>
                {recipe.steps.map((step, i) => {
                    const content = (
                        <>
                            <time>
                                {parseMillisecondsIntoTimeStamp(
                                    step.duration * 1000
                                )}
                            </time>
                            <span>&nbsp;</span>
                            <span>{step.description}</span>
                        </>
                    );

                    // if (isCurrentStep)
                    //     return (
                    //         <li key={i}>
                    //             <strong>{content}</strong>
                    //         </li>
                    //     );

                    return <li key={i}>{content}</li>;
                })}
            </ul>
            {!timer.isRunning && timer.elapsed === 0 && (
                <button onClick={timer.start}>Start</button>
            )}
        </article>
    );
};

export default RecipeTimer;
