import { useCallback, useState, useMemo, useEffect } from "react";

import { useInterval } from "./use-interval";

type Step = {
    description: string;
    duration: number;
};

// Yeah turns out this method is awful. Rewrite to something
// like this: https://stackoverflow.com/a/57981688

const STEPSIZE = 100;

export const useTimer = (userSteps: Step[]) => {
    const [startTime, setStartTime] = useState(0);
    const [overallTime, setOverallTime] = useState(0);
    const [isRunning, setIsRunning] = useState(false);

    const timeElapsedSinceLastStart = useMemo(() => Date.now() - startTime, [
        startTime,
    ]);

    useEffect(() => {
        if (isRunning === false) {
            setOverallTime(
                (overallTime) => overallTime + timeElapsedSinceLastStart
            );
        }
    }, [isRunning]);

    const start = useCallback(() => {
        setIsRunning(true);
        setStartTime(Date.now());
    }, []);

    const stop = useCallback(() => {
        setIsRunning(false);
    }, []);

    const reset = useCallback(() => {
        setIsRunning(false);
        setStartTime(0);
        setOverallTime(0);
    }, []);

    // const [elapsed, setElapsed] = useState(0);
    const [_, setLastTick] = useState(Date.now());
    const tick = () => setLastTick(Date.now());
    useInterval(tick, isRunning ? STEPSIZE : null);

    // // Add a "start" field to every step that was passed in so we
    // // can determine which step is the current:
